mapper.select:
		SELECT a.fmonth_name, a.fmonth_profits, a.results_dailyprofits, a.note, a.login_area, a.work_date, b.results_sum, ROUND((b.results_sum / a.fmonth_profits) * 100, 2) AS results_achievement
		FROM results a INNER JOIN (
    		SELECT fmonth_name, login_area, SUM(results_dailyprofits) AS results_sum FROM results WHERE login_area = #{searchArea} AND work_date BETWEEN #{start_date} AND #{work_date}
		    GROUP BY fmonth_name, login_area ) b
		    ON a.fmonth_name = b.fmonth_name AND a.login_area = b.login_area
			WHERE a.login_area = #{searchArea} AND a.work_date = #{work_date};

select fmonth_profits, results_dailyprofits, results_sum, round((results_sum / fmonth_profits) * 100,2) as results_achievement
from (select sum(fmonth_profits) as fmonth_profits, sum(results_dailyprofits) as results_dailyprofits, (select sum(results_dailyprofits) from results where login_area=#{searchArea} and work_date between #{start_date} and #{work_date}) as results_sum
from results where login_area = #{searchArea} and work_date = #{work_date}) as b;

mapper.insert:
insert results_updatelog(fmonth_name, fmonth_profits, note, login_area, work_date, login_id)
select fmonth_name, fmonth_profits, note, login_area, work_date, #{login_id} from results
where login_area = #{searchArea} and work_date = #{work_date}

update.foreach:
update results
set results_dailyprofits = #{item.dailyprofits},
note = #{item.note}
where fmonth_num=#{item.fmonth_num} and work_date = #{work_date}
